import React from 'react';
import s from './../Dialog.module.css';


const Messages=(props)=>{
    let newRefMessage = React.createRef()
    let addMessage =() =>{


        props.dispatch({type:'ADD-Message'})

    }
    let onchange = ()=>{
        let text = newRefMessage.current.value
        props.dispatch({type:'CHANGE-Message',newmessage: text})
    }
console.log(props)

    return(
        <div>
            <h3>{props.message}</h3>
            <textarea onChange={onchange} ref={newRefMessage}  ></textarea>
            <button onClick={addMessage}>Добавить</button>
        </div>
    )
}
export default Messages;
