import React from 'react';
import s from './../Dialog.module.css';
import {addMessageActionCreator, changeMessageActionCreator} from './../../../Redux/state';

const Messages = (props) => {
    let newRefMessage = React.createRef()
    let addMessage = () => {


        props.dispatch(addMessageActionCreator())

    }
    let onchange = () => {

        let text = newRefMessage.current.value
        props.dispatch(changeMessageActionCreator(text))
    }


    return (
        <div>
            <h3>{props.message}</h3>
            <textarea onChange={onchange} ref={newRefMessage} value={props.newmessage}></textarea>
            <button onClick={addMessage}>Добавить</button>
        </div>
    )
}
export default Messages;
