import React from 'react';
import Post from './Post/Post';

import s from './my_Posts.module.css';

const MyPosts = (props) => {

    let NewMassPosts= props.MyPostData.map(p=>
        <Post messag={p.message} like={p.likes}/>
    )

let newRefText = React.createRef();
    let addPost =() =>{

        let message = newRefText.current.value;
        props.addPosts(message)
        console.log(props)
    }
    let changePost = () =>{

        props.changePost(newRefText.current.value)
    }
    return (

      <div clasName={s.item}>
<div>
    <textarea onChange={changePost} ref={newRefText} ></textarea>
</div>
          <button onClick={addPost}>Кнопка</button>
    {NewMassPosts}
  
  </div>
    )
}

export default MyPosts ;
