const ADD_POST = 'ADD-POST';
const CHANGE_POST = 'CHANGE-POST';
const ADD_MESSAGE = 'ADD-MESSAGE';
const CHANGE_MESSAGE = 'CHANGE-Message';
let store = {
    _state: {
        Profile: {
            mypostdata: [
                {id: 0, message: "Hi how are you", likes: 11},
                {id: 1, message: "Hi how are you", likes: 25},
                {id: 2, message: "it s goot", likes: 23},
                {id: 2, message: "it  a map", likes: 55555553}
            ],
            newPostdata: 'It'


        },
        Message: {
            masdialogs: [
                {id: 1, name: 'Mike'},
                {id: 2, name: 'Viktor'},
                {id: 3, name: 'Mitya2'},
                {id: 4, name: 'Ylia'},
                {id: 5, name: 'Sveta@'}
            ],
            masmessage: [
                {id: 1, message: 'Hi it is coooooool!!!!'},
                {id: 2, message: 'Hi Very'},
                {id: 3, message: 'Hi Verrry'},
                {id: 4, message: 'Hi VBeryyyyyy'},
                {id: 5, message: 'Hi goooood'}
            ],
            newmessage: 'IT'
        },
        Nav: {
            FirstNav:
                [
                    {id: 1, to: 'Profile', item: 'Profile'},
                    {id: 2, to: 'Dialog', item: 'Message'},
                    {id: 3, to: 'News', item: 'News'},
                    {id: 4, to: 'Music', item: 'Music'},
                    {id: 5, to: 'Settings', item: 'Settings'}
                ],
            Friends: [
                {id: 1, name: 'Mike'},
                {id: 1, name: 'Alex'},
                {id: 1, name: 'Viktor'}
            ]
        }


    },
    _rebootS() {
        console.log('calbackRebootS')
    },

    getState() {
        return this._state
    },
    subscribe(observer) {
        this._rebootS = observer
    },


    dispatch(action) {
        if (action.type === ADD_POST) {
            let lastPost = {
                id: 5,
                message: store._state.Profile.newPostdata,
                likes: 6666787686
            }

            this._state.Profile.mypostdata.push(lastPost)
            this._state.Profile.newPostdata = ''
            //mypostdata.push({id:5,message:message,likes:3333})
            this._rebootS(this._state)
        } else if (action.type === CHANGE_POST) {
            this._state.Profile.newPostdata = action.newtext;
            this._rebootS(this._state)
        } else if (action.type === ADD_MESSAGE) {
            let lastMessage = {
                id: 2222,
                message: store._state.Message.newmessage
            }
            this._state.Message.masmessage.push(lastMessage)
            this._state.Message.newmessage = ''
            this._rebootS(this._state)

        } else if (action.type === CHANGE_MESSAGE) {
            this._state.Message.newmessage = action.newmessage;
            this._rebootS(this._state)
        }
    }

}
export const addPostActionCreator = () => ({type: ADD_POST})
export const changePostActionCreator = (text) => ({type: CHANGE_POST, newtext: text})
export const addMessageActionCreator = () => ({type: ADD_MESSAGE})
export const changeMessageActionCreator = (text) => ({type: CHANGE_MESSAGE, newmessage: text})


export default store;
