import React from 'react';
import DialogItem from './DialogItem/DialogItem'
import Messages from './Messages/Messages'
import s from './Dialog.module.css';
import {addMessageActionCreator, changeMessageActionCreator} from "../../Redux/state";

const Dialog = (props) => {

    let NewMasDialogs = props.state.masdialogs.map((d) =>
        <DialogItem id={d.id} name={d.name}/>
    );
    let NewMessages = props.state.masmessage.map((m) =>
        <Messages message={m.message} dispatch={props.dispatch} newmessage={props.state.newmessage}/>
    )

    let newRefMessage = React.createRef()
    let addMessage = () => {
        props.dispatch(addMessageActionCreator())
    }
    let onchange = () => {
        let text = newRefMessage.current.value
        props.dispatch(changeMessageActionCreator(text))
    }


    return (
        <div className={s.main_container}>
            <div className={s.header}>
                DIALOGS
            </div>
            <div className={s.dialog_container}>
                <div className={s.left}>
                    {NewMasDialogs}
                </div>
                <div className='messages'>
                    <div>{NewMessages}</div>
                    <div>
                        <h3>{props.message}</h3>
                        <textarea onChange={onchange} ref={newRefMessage} value={props.newmessage}></textarea>
                        <button onClick={addMessage}>Добавить</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dialog;
